function load(a){for(var i=$(".main"),t=_.template(game_div_template),e=0;e<a.length;e++){var v=a[e],d=t(v);i.append(d)}var l=a.length;$(".menu").html(l)}var game_div_template='<div class="game">  <div class="title"><%= Title %></div>  <div class="coverimg"><img src="<%= CoverImg %>"/></div>  <div class="metadata">    <div class="installed"><%= Installed %></div>    <div class="status"><%= Status %></div>  </div></div>';